mapper.select:
		select * from sj_group_members where 1 = 1
		
			and id = #{id}
		
		
			and group_id = #{group_id}
		
		
			and begin_time = #{begin_time}
		
		
			and end_time = #{end_time}
		
		
			and user_id = #{user_id}
		
		
			and duty = #{duty}
		
		
			and state = #{state}
		
		
			and temp1 = #{temp1}
		
		
			and temp2 = #{temp2}
		
		
			and temp3 = #{temp3}

		select count(*) from sj_group_members where 1 = 1
		
			and id = #{id}
		
		
			and group_id = #{group_id}
		
		
			and begin_time = #{begin_time}
		
		
			and end_time = #{end_time}
		
		
			and user_id = #{user_id}
		
		
			and duty = #{duty}
		
		
			and state = #{state}
		
		
			and temp1 = #{temp1}
		
		
			and temp2 = #{temp2}
		
		
			and temp3 = #{temp3}

mapper.update:
UPDATE sj_group_members
id=#{id}, group_id=#{group_id}, begin_time=#{begin_time}, end_time=#{end_time}, user_id=#{user_id}, duty=#{duty}, state=#{state}, temp1=#{temp1}, temp2=#{temp2}, temp3=#{temp3}
WHERE id=#{id}

mapper.select:
   		select b.USER_ID from sj_groups a,sj_group_members b 
		where a.id=b.GROUP_ID and length(b.user_id)=32 
		and a.practice_id=#{practice_id}
