mapper.update:
UPDATE sj_practice_task
practice_code=#{practice_code}, task_name=#{task_name}, tea_id=#{tea_id}, begin_time=#{begin_time}, end_time=#{end_time}, create_time=#{create_time}, task_desc=#{task_desc}, task_place=#{task_place}, file_id=#{file_id}, task_type=#{task_type}, parent_id=#{parent_id}, weight_summary=#{weight_summary}, weight_thesis=#{weight_thesis}, weight_evaluate=#{weight_evaluate}, state=#{state}, grade=#{grade}, scope=#{scope}, studyLength = #{studyLength} , temp1=#{temp1}, temp2=#{temp2}, temp3=#{temp3}
WHERE id=#{id}

mapper.select:
select b.USER_ID from sj_groups a,sj_group_members b
where a.id=b.GROUP_ID and length(b.user_id)=32
and a.practice_id=#{id}

         
