mapper.select:
		select * from sj_files where 1 = 1
		
			and id = #{id}
		
		
			and file_name = #{file_name}
		
		
			and file_code = #{file_code}
		
		
			and file_type = #{file_type}
		
		
			and upload_time = #{upload_time}
		
		
			and description = #{description}
		
		
			and position = #{position}
		
		
			and file_size = #{file_size}
		
		
			and stu_id = #{stu_id}

mapper.update:
UPDATE sj_files
id=#{id}, file_name=#{file_name}, file_code=#{file_code}, file_type=#{file_type}, upload_time=#{upload_time}, description=#{description}, position=#{position}, file_size=#{file_size}, stu_id=#{stu_id}
WHERE id=#{id}

mapper.select:
		select count(*) from sj_files where 1 = 1
		
			and id = #{id}
		
		
			and file_name = #{file_name}
		
		
			and file_code = #{file_code}
		
		
			and file_type = #{file_type}
		
		
			and upload_time = #{upload_time}
		
		
			and description = #{description}
		
		
			and position = #{position}
		
		
			and file_size = #{file_size}
		
		
			and stu_id = #{stu_id}
