mapper.select:
		select * from sj_summary_qnanswer where 1 = 1
		
			and id = #{id}
		
		
			and startdate = #{startdate}
		
		
			and enddate = #{enddate}
		
		
			and user_id = #{user_id}
		
		
			and questionnaire_id = #{questionnaire_id}
		
		
			and answerDate = #{answerDate}
		
		
			and score = #{score}
			
		
			and practiceTasksID = #{practiceTasksID}
					
		
			and temp1 = #{temp1}
		
		
			and temp2 = #{temp2}
		
		
			and temp3 = #{temp3}
		
		
			and temp4 = #{temp4}
		
		
			and temp5 = #{temp5}

		select count(*) from sj_summary_qnanswer where 1 = 1
		
			and id = #{id}
		
		
			and startdate = #{startdate}
		
		
			and enddate = #{enddate}
		
		
			and user_id = #{user_id}
		
		
			and questionnaire_id = #{questionnaire_id}
		
		
			and answerDate = #{answerDate}
		
		
			and score = #{score}
			
		
			and practiceTasksID = #{practiceTasksID}
			
		
			and temp1 = #{temp1}
		
		
			and temp2 = #{temp2}
		
		
			and temp3 = #{temp3}
		
		
			and temp4 = #{temp4}
		
		
			and temp5 = #{temp5}

mapper.update:
UPDATE sj_summary_qnanswer
id=#{id}, startdate=#{startdate}, enddate=#{enddate}, user_id=#{user_id}, questionnaire_id=#{questionnaire_id}, answerDate=#{answerDate}, score=#{score}, practiceTasksID=#{practiceTasksID}, temp1=#{temp1}, temp2=#{temp2}, temp3=#{temp3}, temp4=#{temp4}, temp5=#{temp5}
WHERE id=#{id}

mapper.delete:
delete from sj_summary_qnanswer where 1 = 1
		
	and id = #{id}


	and startdate = #{startdate}


	and enddate = #{enddate}


	and user_id = #{user_id}


	and questionnaire_id = #{questionnaire_id}


	and answerDate = #{answerDate}


	and score = #{score}
	

	and practiceTasksID = #{practiceTasksID}


	and temp1 = #{temp1}


	and temp2 = #{temp2}


	and temp3 = #{temp3}


	and temp4 = #{temp4}


	and temp5 = #{temp5}

mapper.select:
		select count(*) from sj_summary_qnanswer where 1 = 1
		
			and id = #{id}
		
		
			and questionnaire_id = #{questionnaire_id}
		
		
			and practiceTasksID = #{practiceTasksID}
			
		AND enddate IS NOT NULL
